mapper.select:
SELECT ct_sub FROM tbl_category
WHERE ct_main = #{ct_main}
ORDER BY ct_code DESC

SELECT ct_code, ct_sub FROM tbl_category
WHERE ct_main = #{ct_main}
ORDER BY ct_code DESC

mapper.insert:
INSERT INTO tbl_item (
	it_code, it_decode, it_seid, it_ctcode, it_title,
	it_price, it_shippingfee, it_content, it_photo
) VALUES (
	#{it_code}, #{it_decode}, #{it_seid}, #{it_ctcode},
	#{it_title}, #{it_price}, #{it_shippingfee},
	#{it_content}, #{it_photo}
)
