mapper.select:
SELECT * FROM tbl_cart
WHERE cr_buyerid = #{cr_buyerid}
ORDER BY cr_code DESC

mapper.update:
UPDATE tbl_cart SET 
cr_buyerid = #{cr_buyerid}
cr_itcode = #{cr_itcode}
cr_option = #{cr_option}
cr_price = #{cr_price}
cr_amount = #{cr_amount}
cr_shippingfee = #{cr_shippingfee}
WHERE cr_code = #{cr_code}
