mapper.select:
SELECT * FROM tbl_cart
WHERE cr_buyerid = #{cr_buyerid}
ORDER BY cr_code DESC

mapper.update:
UPDATE tbl_cart(q_writer, q_itcode, q_title, q_content, q_date, q_time)
SET 
q_writer = #{q_writer}
q_itcode = #{q_itcode}
q_title = #{q_title}
q_content = #{q_content}
q_date = #{q_date}
q_time = #{q_time}
WHERE q_code = #{q_code}
