mapper.select:
SELECT * FROM view_qna
WHERE q_itcode = #{q_itcode}
ORDER BY q_time DESC, q_date DESC;

SELECT * FROM tbl_qna
WHERE q_code = #{q_code}
ORDER BY q_time DESC, q_date DESC;

mapper.update:
UPDATE tbl_qna SET 
q_writer = #{q_writer}
q_itcode = #{q_itcode}
q_title = #{q_title}
q_content = #{q_content}
q_date = #{q_date}
q_time = #{q_time}
WHERE q_code = #{q_code}
