mapper.select:
SELECT * FROM view_review
WHERE r_itcode = #{r_itcode}
ORDER BY r_time DESC, r_date DESC;

mapper.update:
UPDATE tbl_review(r_writer, r_itcode, r_content, r_date, r_score)
SET 
r_writer = #{r_writer}
r_itcode = #{r_itcode}
r_content = #{r_content}
r_date = #{r_date}
r_score = #{r_score}
WHERE r_code = #{r_code}
